import React, { useState, useEffect } from 'react';
import { subjects, getAllYears } from '../data/subjects';
import Message from './common/Message';
import './SubjectSelector.css';

const SubjectSelector = ({ selectedSubjects, onSelectionChange }) => {
  const [selectedYear, setSelectedYear] = useState(null);
  const [selectedSemester, setSelectedSemester] = useState(null);
  const [availableSubjects, setAvailableSubjects] = useState([]);
  const [checkedSubjects, setCheckedSubjects] = useState(new Set());

  const years = getAllYears();

  // Update available subjects when year/semester changes
  useEffect(() => {
    if (selectedYear && selectedSemester) {
      const filtered = subjects.filter(
        s => s.year === selectedYear && 
        s.semester === selectedSemester && 
        !s.toDefine
      );
      setAvailableSubjects(filtered);
      
      // Update checked subjects based on current selection
      const currentIds = new Set(selectedSubjects.map(s => s.id));
      setCheckedSubjects(currentIds);
    } else {
      setAvailableSubjects([]);
      setCheckedSubjects(new Set());
    }
  }, [selectedYear, selectedSemester, selectedSubjects]);

  const handleYearChange = (year) => {
    setSelectedYear(year);
    if (!selectedSemester) {
      setSelectedSemester(1); // Default to first semester
    }
  };

  const handleSemesterChange = (semester) => {
    setSelectedSemester(semester);
  };

  const handleSubjectToggle = (subject) => {
    const newChecked = new Set(checkedSubjects);
    
    if (newChecked.has(subject.id)) {
      newChecked.delete(subject.id);
    } else {
      newChecked.add(subject.id);
    }
    
    setCheckedSubjects(newChecked);
    
    // Update parent component
    const selected = availableSubjects.filter(s => newChecked.has(s.id));
    onSelectionChange(selected);
  };

  const formatDays = (days) => {
    const dayNames = { 'L': 'Lun', 'M': 'Mar', 'J': 'Jue', 'V': 'Vie' };
    return days.map(d => dayNames[d] || d).join(', ');
  };

  return (
    <div className="subject-selector">
      <h2>Seleccionar Asignaturas</h2>
      
      <div className="filters">
        <div className="filter-group">
          <label>Año:</label>
          <select 
            value={selectedYear || ''} 
            onChange={(e) => handleYearChange(Number(e.target.value))}
            className="filter-select"
          >
            <option value="">Seleccionar año</option>
            {years.map(year => (
              <option key={year} value={year}>{year}° Año</option>
            ))}
          </select>
        </div>

        {selectedYear && (
          <div className="filter-group">
            <label>Semestre:</label>
            <div className="semester-buttons">
              <button
                className={`semester-btn ${selectedSemester === 1 ? 'active' : ''}`}
                onClick={() => handleSemesterChange(1)}
              >
                1° Semestre
              </button>
              <button
                className={`semester-btn ${selectedSemester === 2 ? 'active' : ''}`}
                onClick={() => handleSemesterChange(2)}
              >
                2° Semestre
              </button>
            </div>
          </div>
        )}
      </div>

      {selectedYear && selectedSemester && (
        <div className="subjects-list">
          {availableSubjects.length === 0 ? (
            <Message variant="blue">
              <p>No hay asignaturas disponibles para esta combinación.</p>
            </Message>
          ) : (
            <>
              <div className="subjects-header">
                <h3>Asignaturas disponibles ({availableSubjects.length})</h3>
                <span className="selected-count">
                  {checkedSubjects.size} seleccionada{checkedSubjects.size !== 1 ? 's' : ''}
                </span>
              </div>
              <div className="subjects-grid">
                {availableSubjects.map(subject => (
                  <div 
                    key={subject.id} 
                    className={`subject-card ${checkedSubjects.has(subject.id) ? 'selected' : ''}`}
                    onClick={() => handleSubjectToggle(subject)}
                  >
                    <div className="subject-card-header">
                      <input
                        type="checkbox"
                        checked={checkedSubjects.has(subject.id)}
                        onChange={() => handleSubjectToggle(subject)}
                        onClick={(e) => e.stopPropagation()}
                      />
                      <h4>{subject.name}</h4>
                    </div>
                    <div className="subject-card-body">
                      <div className="subject-info">
                        <span className="info-item">
                          <i className="bi bi-calendar"></i>
                          {formatDays(subject.days)}
                        </span>
                        {subject.schedule && (
                          <span className="info-item">
                            <i className="bi bi-clock"></i>
                            {subject.schedule.start} - {subject.schedule.end}
                          </span>
                        )}
                        {subject.notes && (
                          <span className="info-item notes">
                            <i className="bi bi-info-circle"></i>
                            {subject.notes}
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>
      )}

      {!selectedYear && (
        <Message variant="blue">
          <p>Selecciona un año y semestre para ver las asignaturas disponibles.</p>
        </Message>
      )}
    </div>
  );
};

export default SubjectSelector;
